mapper.update:
update bbs     
set wname=#{wname},   
    title=#{title},   
    content=#{content}  
where bbsno=#{bbsno}

mapper.select:
SELECT bbsno, grpno, title, indent, ansnum 
FROM bbs                                   
WHERE bbsno = #{bbsno}

select count(bbsno) as cnt 
from bbs                   
where bbsno = #{bbsno}
and passwd = #{passwd}
